import { Component, OnInit } from '@angular/core';
import { Product } from './model/product';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'app works!';
  total = 0;
  size = '20px';
  basket: Product[] = [];

  products: Product[] = [
    new Product("Angular", "Formation Angular 2", "data:image/png;base64,", 5, 20),
    new Product("Java", "Formation java", "data:image/png;base64,", 8, 3),
    new Product("Spark", "Formation Spark", "data:image/jpeg;base64,", 3, 2),
    new Product("Cassandra", "Formation Cassandra", "data:image/png;base64,", 12, 5)
  ]

  public onAddToBasket(data: Product): void {
    console.log(`New product in basket with price ${data.getPrice}`);
    this.total += data.getPrice;
    this.basket.push(data);

    this.size = "40px";
    setTimeout(() => {
      this.size = "20px";
      console.log("restore size " + this.size)
    }, 3000);
  }

  ngOnInit() {
  }


}
